import Image from 'next/image'

interface OptimizedImageProps {
  src: string
  alt: string
  width?: number
  height?: number
  className?: string
  priority?: boolean
  placeholder?: 'blur' | 'empty'
  blurDataURL?: string
  sizes?: string
}

export function OptimizedImage({
  src,
  alt,
  width,
  height,
  className = '',
  priority = false,
  placeholder = 'empty',
  blurDataURL,
  sizes = '(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw'
}: OptimizedImageProps) {
  // Generate a simple blur placeholder if none provided
  const defaultBlurDataURL = 'data:image/jpeg;base64,'

  // Build props object conditionally to avoid undefined values
  const imageProps: any = {
    src,
    alt,
    className,
    priority,
    placeholder,
    sizes,
    style: {
      maxWidth: '100%',
      height: 'auto',
    }
  }

  // Only add width/height if they are defined
  if (width !== undefined) {
    imageProps.width = width
  }
  if (height !== undefined) {
    imageProps.height = height
  }

  // Only add blurDataURL if using blur placeholder
  if (placeholder === 'blur') {
    imageProps.blurDataURL = blurDataURL || defaultBlurDataURL
  }

  return <Image {...imageProps} />
}
